function showPortfolio() {

    //console.log(isPortfolioOpen + " HERE");
    if (isPortfolioOpen == false) {
        document.getElementById("button-layout").style = ("display: block;");
        document.getElementById("dotHome").style = ("display: block;");
        currentSlide(0);
        document.getElementById("portButton").innerHTML = "Click to close portfolio";
        isPortfolioOpen = true;
    } else if (isPortfolioOpen == true) {
        document.getElementById("button-layout").style = ("display: none;");
        document.getElementById("dotHome").style = ("display: none;");
        document.getElementById("portButton").innerHTML = "Click to open portfolio";
        isPortfolioOpen = false;
    }
}


function showSlides(n) {
    var i;
    var slides = document.getElementsByClassName("mySlides");
    var dots = document.getElementsByClassName("dot");
    for (i = 0; i < slides.length; i++) {
        slides[i].style.display = "none";
    }
    curSlide++;
    console.log(curSlide);
    if (curSlide > slides.length) { curSlide = 1 }
    for (i = 0; i < dots.length; i++) {
        dots[i].className = dots[i].className.replace(" active", "");
    }
    slides[curSlide - 1].style.display = "block";
    dots[curSlide - 1].className += " active";
    setTimeout(showSlides, 8000); // Change image every 2 seconds

}